
import { importer, loaded } from '../../importer'
import models from '../../models'
import { expect } from '../utils'

describe('Unmined Jobs Processor', () => {
  after(async () => {
        await models.BoostJob.destroy({where: {}});
        await models.BoostWork.destroy({where: {}});
        await models.Event.destroy({where: {}});
    });
    
  before(async () => {

    await loaded()

  })

  it("should import a matching proof spending a job output", async () => {

    const proof_tx_hex = ""

    const { proofs } = await importer.importProof(proof_tx_hex)

    const record = await models.BoostWork.findOne({
      where: {
        tx_hex: proof_tx_hex
      }
    })

    expect(record.tx_hex).to.be.equal(proof_tx_hex)

    expect(record.txid)

    expect(proofs)

  })

})
