
import { expect } from '../utils'

import models  from '../../models'

describe("Boost Proof Model", () => {

    it('should set the profitability automatically', async () => {

        const params = {
            content: '00000000000000000000000000000000000000000068656c6c6f20776f726c64',
            difficulty: 1,
            tag: 'askbitcoin',
            spend_txid: '00000000000000000000000000000000000000000068656c6c6f20776f726c64',
            job_txid: '00000000000000000000000000000000000000000068656c6c6f20776f726c64',
            spend_vout: 0,
            job_vout: 0,
            value: 100000000,
            timestamp: new Date(),
            script: ''
        }

        const record = await models.BoostWork.create(params)

        expect(record.profitability).to.be.greaterThan(0)

    })

})
